    private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
        user_2.setVisible(true);
        user_home.dispose();
        String sno=txt_user_sno.getText();
        String name=txt_user_name.getText();
        txt_user2_sno.setText(sno);
        txt_user2_name.setText(name);
        My_connection obj=new My_connection();
        String q="select * from a"+sno+";";
        ResultSet rs=obj.fetch(q);
        DefaultTableModel dtm=(DefaultTableModel)tbl_user2_record.getModel();
         try{
            while(rs.next()){
                String isbn=rs.getString("isbn");
                String doi=rs.getString("date_of_issue");
                String dor=rs.getString("date_of_return");
                String fine=rs.getString("fine");
                String remark=rs.getString("remark");
                dtm.addRow(new Object[]{isbn,doi,dor,fine,remark});
            }
            tbl_user2_record.setModel(dtm);
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null,e.getMessage());
        }
        
        
    }//GEN-LAST:event_jButton2MouseClicked

