    private void btnloginMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnloginMouseClicked
        My_connection obj=new My_connection();
        String pass="";
        String password=new String(txtpassword.getPassword());
        
        
        if(login==1)
        {
            
            String q="select * from password where serial_no='admin';";
            ResultSet rs=obj.fetch(q);
            try{
                if(rs.next()){
                     pass=rs.getString("password");
                }
               
                if(password.equals(pass))
                {
                    admin_home.setVisible(true);
                    this.dispose();
                }
                else
                    JOptionPane.showMessageDialog(null,"passwor didn't match");
               
            }
            catch(Exception e){
                JOptionPane.showMessageDialog(null,e);
            }
        }
        if(login==2)
        {
            String sno=txtusername.getText();
            String name="";
            String address="";
            String contact="";
            String dob="";
            String q="select * from password where serial_no='"+sno+"';";
            ResultSet rs=obj.fetch(q);
            try{
                if(rs.next())
                    {
                     pass=rs.getString("password");
                    }
                if(password.equals(pass)){
                    q="select * from user where serial_no='"+sno+"';";
                    rs=obj.fetch(q);
                    if(rs.next()){
                        name=rs.getString("name");
                        address=rs.getString("address");
                        contact=rs.getString("contact_no");
                        dob=rs.getString("dob");
                    }
                    user_home.setVisible(true);
                    this.dispose();
                    txt_user_sno.setText(sno);
                    txt_user_name.setText(name);
                    txt_user_address.setText(address);
                    txt_user_cno.setText(contact);
                    txt_user_dob.setText(dob);
                    
                    
                }
                else
                    JOptionPane.showMessageDialog(null,"passwor didn't match");
                        
                
            }
            catch(Exception e){
                JOptionPane.showMessageDialog(null,e);
            }
        }
        
    }//GEN-LAST:event_btnloginMouseClicked

